package com.quantum_guys.dncc_eco_sync;import static com.quantum_guys.dncc_eco_sync.global.UserDB.TYPE_ADMIN;import static com.quantum_guys.dncc_eco_sync.global.UserDB.TYPE_DRIVER;import static com.quantum_guys.dncc_eco_sync.global.UserDB.TYPE_LANDFILL_MANAGER;import static com.quantum_guys.dncc_eco_sync.global.UserDB.TYPE_STS_MANAGER;import android.app.ActivityOptions;import android.content.Intent;import android.content.res.Configuration;import android.os.Bundle;import androidx.appcompat.app.AppCompatActivity;import com.quantum_guys.dncc_eco_sync.activity.LoginActivity;import com.quantum_guys.dncc_eco_sync.global.LanguageSetting;import com.quantum_guys.dncc_eco_sync.global.UserDB;import java.util.Locale;public class Splash extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        Locale locale = new Locale(LanguageSetting.getLanguage(this));        Locale.setDefault(locale);        Configuration config = getBaseContext().getResources().getConfiguration();        config.setLocale(locale);        getBaseContext().getResources().updateConfiguration(config,                getBaseContext().getResources().getDisplayMetrics());        int type = UserDB.loadData(getApplicationContext());        boolean loggedIn = UserDB.loggedIn(getApplicationContext());        if (type == TYPE_ADMIN) {            if (loggedIn) {                startActivity(new Intent(this, MainActivity.class), ActivityOptions.makeSceneTransitionAnimation(this).toBundle());            } else {                startActivity(new Intent(this, LoginActivity.class), ActivityOptions.makeSceneTransitionAnimation(this).toBundle());            }            finish();        } else if (type == TYPE_STS_MANAGER) {            if (loggedIn) {                startActivity(new Intent(this, MainActivity.class), ActivityOptions.makeSceneTransitionAnimation(this).toBundle());            } else {                startActivity(new Intent(this, LoginActivity.class), ActivityOptions.makeSceneTransitionAnimation(this).toBundle());            }            finish();        }else if (type == TYPE_LANDFILL_MANAGER) {            if (loggedIn) {                startActivity(new Intent(this, MainActivity.class), ActivityOptions.makeSceneTransitionAnimation(this).toBundle());            } else {                startActivity(new Intent(this, LoginActivity.class), ActivityOptions.makeSceneTransitionAnimation(this).toBundle());            }            finish();        } else if (type == TYPE_DRIVER) {            if (loggedIn) {                startActivity(new Intent(this, MainActivity.class), ActivityOptions.makeSceneTransitionAnimation(this).toBundle());            } else {                startActivity(new Intent(this, LoginActivity.class), ActivityOptions.makeSceneTransitionAnimation(this).toBundle());            }            finish();        } else {            startActivity(new Intent(this, LoginActivity.class), ActivityOptions.makeSceneTransitionAnimation(this).toBundle());            finish();        }          }}