package com.quantum_guys.dncc_eco_sync.adapter;import android.annotation.SuppressLint;import android.app.Activity;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.LinearLayout;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.github.marlonlom.utilities.timeago.TimeAgo;import com.quantum_guys.dncc_eco_sync.R;import com.quantum_guys.dncc_eco_sync.model.Notification;import java.util.List;public class NotificationAdapter extends RecyclerView.Adapter<NotificationAdapter.ViewHolder> {    private static final String TAG = "AddStationAdapter";    Activity context;    List<Notification> notificationList;    public NotificationAdapter(Activity context, List<Notification> t) {        this.context = context;        this.notificationList = t;    }    @NonNull    @Override    public ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = LayoutInflater.from(context).inflate(R.layout.item_notification, parent, false);        return new ViewHolder(view);    }    @Override    public long getItemId(int position) {        return position;    }    @Override    public int getItemViewType(int position) {        return position;    }    @SuppressLint("SetTextI18n")    @Override    public void onBindViewHolder(@NonNull final ViewHolder holder, int position) {        Notification notification = notificationList.get(position);        holder.name.setText(notification.getMessage());        holder.amount.setVisibility(View.GONE);        holder.time.setText(TimeAgo.using(-1 * notification.getTimeStamp()));        holder.lastStation.setText(notification.getMessage());        holder.itemView.setOnClickListener(view -> {            /*progressDialog.show();            db.child("Routes").child(trip.getRouteId()).addValueEventListener(new ValueEventListener() {                @Override                public void onDataChange(@NonNull DataSnapshot snapshot) {                    Route route = snapshot.getValue(Route.class);                    context.startActivity(new Intent(context, BusSeatStatusActivity.class)                            .putExtra("route", route)                            .putExtra("trip", trip));                    context.finish();                }                @Override                public void onCancelled(@NonNull DatabaseError error) {                    progressDialog.dismiss();                }            });*/        });    }    @Override    public int getItemCount() {        return notificationList.size();    }    public static class ViewHolder extends RecyclerView.ViewHolder {        TextView name, amount, lastStation, time;        LinearLayout active;        public ViewHolder(View itemView) {            super(itemView);            name = itemView.findViewById(R.id.textView5);            lastStation = itemView.findViewById(R.id.textView31);            amount = itemView.findViewById(R.id.textView33);            time = itemView.findViewById(R.id.textView32);            active = itemView.findViewById(R.id.dds);        }    }}