package com.quantum_guys.dncc_eco_sync.adapters;import android.annotation.SuppressLint;import android.app.Activity;import android.app.ProgressDialog;import android.content.Intent;import android.os.Build;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.LinearLayout;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.annotation.RequiresApi;import androidx.recyclerview.widget.RecyclerView;import com.quantum_guys.dncc_eco_sync.R;import com.quantum_guys.dncc_eco_sync.models.Trip;import com.quantum_guys.dncc_eco_sync.ui.activities.MainActivity;import com.quantum_guys.dncc_eco_sync.ui.activities.nearbysts.MapView;import java.util.List;public class TripAdapter extends RecyclerView.Adapter<TripAdapter.ViewHolder> {    private static final String TAG = "AddStationAdapter";    Activity context;    List<Trip> tripList;    ProgressDialog progressDialog;    public TripAdapter(Activity context, List<Trip> t) {        this.context = context;        this.tripList = t;    }    @NonNull    @Override    public ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = LayoutInflater.from(context).inflate(R.layout.item_trip, parent, false);        return new ViewHolder(view);    }    @Override    public long getItemId(int position) {        return position;    }    @Override    public int getItemViewType(int position) {        return position;    }    @RequiresApi(api = Build.VERSION_CODES.R)    @SuppressLint("SetTextI18n")    @Override    public void onBindViewHolder(@NonNull final ViewHolder holder, int position) {        Trip trip = tripList.get(position);        holder.name.setText(trip.getVehicleRegNo());        holder.amount.setText("à§³" + trip.getTotalAmount());        holder.licenceNo.setText("Vehicle: " + trip.getVehicleRegNo());        holder.time.setText(trip.getStartTime());        if (!trip.isCompleted()) holder.active.setVisibility(View.VISIBLE);        try {            holder.lastStation.setText("Last STS: " + trip.getLastStsName());        } catch (Exception d) {            holder.lastStation.setText("Nothing to Show");        }        holder.itemView.setOnClickListener(view -> {            progressDialog.show();            if (!trip.isCompleted()) {                context.startActivity(new Intent(context, MapView.class)                        .putExtra("trip", trip));            } else {                context.startActivity(new Intent(context, MainActivity.class)                        .putExtra("trip", trip));            }        });    }    @Override    public int getItemCount() {        return tripList.size();    }    public static class ViewHolder extends RecyclerView.ViewHolder {        TextView name, amount, lastStation, time, licenceNo;        LinearLayout active;        public ViewHolder(View itemView) {            super(itemView);            name = itemView.findViewById(R.id.textView54);            lastStation = itemView.findViewById(R.id.textView314);            amount = itemView.findViewById(R.id.textView334);            time = itemView.findViewById(R.id.textView324);            active = itemView.findViewById(R.id.dds4);            licenceNo = itemView.findViewById(R.id.textView684);        }    }}