package com.quantum_guys.dncc_eco_sync.db;import android.content.Context;import androidx.room.Database;import androidx.room.Room;import androidx.room.RoomDatabase;import com.quantum_guys.dncc_eco_sync.dao.DaoAccess;import com.quantum_guys.dncc_eco_sync.messege.model.Chat;import com.quantum_guys.dncc_eco_sync.messege.model.Chatlist;import com.quantum_guys.dncc_eco_sync.models.Post;import com.quantum_guys.dncc_eco_sync.models.Users;import com.quantum_guys.dncc_eco_sync.ui.activities.quiz.BattleModel;import com.quantum_guys.dncc_eco_sync.ui.activities.quiz.Result;@Database(entities = {Users.class, BattleModel.class, Result.class, Post.class, Chatlist.class, Chat.class}, version = 1, exportSchema = false)public abstract class QuizResultDatabase extends RoomDatabase {    private static QuizResultDatabase instance;    public static synchronized QuizResultDatabase getInstance(Context context) {        if (instance == null) {            instance = Room.databaseBuilder(context.getApplicationContext(),                            QuizResultDatabase.class, "dncc_eco_sync")                    .allowMainThreadQueries()                    .fallbackToDestructiveMigration()                    .build();        }        return instance;    }    public abstract DaoAccess daoAccess();}