package com.quantum_guys.dncc_eco_sync.global;import static android.content.Context.MODE_PRIVATE;import android.content.Context;import android.content.SharedPreferences;public class LanguageSetting {    private static final String SHARED_PREFS = "sharedPrefs";    private static final String KEY = "lang";    public static void setLanguage(Context context, String TEXT) {        SharedPreferences sharedPreferences = context.getSharedPreferences(SHARED_PREFS, MODE_PRIVATE);        SharedPreferences.Editor editor = sharedPreferences.edit();        editor.putString(KEY, TEXT);        editor.apply();    }    public static String getLanguage(Context context) {        SharedPreferences sharedPreferences = context.getSharedPreferences(SHARED_PREFS, MODE_PRIVATE);        return sharedPreferences.getString(KEY, "en");    }}