package com.quantum_guys.dncc_eco_sync.global;import static android.content.Context.MODE_PRIVATE;import android.content.Context;import android.content.SharedPreferences;public class UserType {    private static final String SHARED_PREFS = "sharedPrefs";    private static final String KEY = "userType";    public static int TYPE_ADMIN = 1;    public static int TYPE_DRIVER = 2;    public static int TYPE_STS_MANAGER = 3;    public static int TYPE_LANDFILL_MANAGER = 4;    public static void saveData(Context context, int TEXT) {        SharedPreferences sharedPreferences = context.getSharedPreferences(SHARED_PREFS, MODE_PRIVATE);        SharedPreferences.Editor editor = sharedPreferences.edit();        editor.putInt(KEY, TEXT);        editor.apply();    }    public static int loadData(Context context) {        SharedPreferences sharedPreferences = context.getSharedPreferences(SHARED_PREFS, MODE_PRIVATE);        return sharedPreferences.getInt(KEY, 0);    }}