package com.quantum_guys.dncc_eco_sync.util;import android.util.Base64;import java.nio.charset.StandardCharsets;public class Encoder {    public static String encode(String token) {        byte[] encodedBytes = Base64.encode(token.getBytes(), 0);        return new String(encodedBytes, StandardCharsets.UTF_8);    }    public static String decode(String token) {        byte[] decodedBytes = Base64.decode(token.getBytes(), 0);        return new String(decodedBytes, StandardCharsets.UTF_8);    }}