package com.quantum_guys.dncc_eco_sync.utils;import androidx.room.TypeConverter;import com.google.gson.Gson;import com.google.gson.reflect.TypeToken;import com.quantum_guys.dncc_eco_sync.ui.activities.quiz.Player;import java.lang.reflect.Type;public class PlayerConverter {    private static final Gson gson = new Gson();    @TypeConverter    public static Player stringToList(String data) {        if (data == null) {            return null;        }        Type listType = new TypeToken<Player>() {        }.getType();        return gson.fromJson(data, listType);    }    @TypeConverter    public static String ListToString(Player someObjects) {        return gson.toJson(someObjects);    }}