package com.quantum_guys.dncc_eco_sync.viewmodel;import android.app.Application;import androidx.annotation.NonNull;import androidx.lifecycle.AndroidViewModel;import androidx.lifecycle.LiveData;import com.quantum_guys.dncc_eco_sync.messege.model.Chatlist;import com.quantum_guys.dncc_eco_sync.repository.ChatRepository;import java.util.List;public class ChatViewModel extends AndroidViewModel {    public LiveData<List<Chatlist>> chats;    ChatRepository repository;    public ChatViewModel(@NonNull Application application) {        super(application);        repository = new ChatRepository(application);        chats = repository.getAllUser();    }    public LiveData<List<Chatlist>> getAllUser() {        return repository.getAllUser();    }    public void updateUser(Chatlist admin) {        repository.updateUser(admin);    }    public void insertUser(Chatlist user) {        repository.insertUser(user);    }    public void deleteAdmin(String uniqueId) {        repository.deleteChat(uniqueId);    }}