package com.quantum_guys.dncc_eco_sync.viewmodel;import android.app.Application;import androidx.annotation.NonNull;import androidx.lifecycle.AndroidViewModel;import androidx.lifecycle.LiveData;import com.quantum_guys.dncc_eco_sync.models.Post;import com.quantum_guys.dncc_eco_sync.repository.PostRepository;import java.util.List;public class PostViewModel extends AndroidViewModel {    public final LiveData<List<Post>> posts;    final PostRepository repository;    public PostViewModel(@NonNull Application application) {        super(application);        repository = new PostRepository(application);        posts = repository.getAllPosts();    }    public void insert(Post result) {        repository.insert(result);    }    public void delete(Post result) {        repository.delete(result);    }}